<?phpfunction themeslug_customize_register( $wp_customize ) {//------------------- Tao section Footer	// Tao Footer section.    $wp_customize->add_section(    	'footer',        array(            'title'      => __( 'Footer', '' ),            'priority'   => 30,        )    );    // Tao background setting.    $wp_customize->add_setting(    	'footer_background',    	array(			'type'              => 'option', // 'option' || 'theme_mod			'capability'        => 'edit_theme_options',			'section'           => 'footer',		)	);    // Tao background control.	$wp_customize->add_control(		new WP_Customize_Color_Control(			$wp_customize,			'footer_background',			array(				'label'    => __( 'Background Color', 'phungdat' ),				'section'  => 'footer',				'settings' => 'footer_background',			)		)	);	// Tao logo image footer setting.    $wp_customize->add_setting(    	'footer_logo_image',    	array(    		'type'              => 'option', // 'option' || 'theme_mod			'capability'        => 'edit_theme_options',			'section'           => 'footer',		)	);    // Tao logo image footer control.	$wp_customize->add_control(		new WP_Customize_Image_Control(			$wp_customize,			'footer_logo_image',			array(				'label'    => __( 'Logo Footer', 'phungdat' ),				'section'  => 'footer',				'settings' => 'footer_logo_image',			)		)	);	// Tao description setting.    $wp_customize->add_setting(    	'footer_description',    	array(			'type'              => 'option', // 'option' || 'theme_mod			'capability'        => 'edit_theme_options',			'section'           => 'footer',		)	);    // Tao description control.	$wp_customize->add_control(		new WP_Customize_Control(			$wp_customize,			'footer_description',			array(				'label'    => __( 'Description', 'phungdat' ),				'section'  => 'footer',				'settings' => 'footer_description',				'type'     => 'textarea',			)		)	);    // Tao copyright setting.    $wp_customize->add_setting(    	'footer_copyright',    	array(			'type'              => 'option', // 'option' || 'theme_mod			'capability'        => 'edit_theme_options',			'section'           => 'footer',		)	);    // Tao copyright control.	$wp_customize->add_control(		new WP_Customize_Control(			$wp_customize,			'footer_copyright',			array(				'label'    => __( 'Copyright', 'phungdat' ),				'section'  => 'footer',				'settings' => 'footer_copyright',				'type'     => 'textarea',			)		)	);//-------------------- Tao section Title H1	// Tao Title section	$wp_customize->add_section(    	'title_header',        array(            'title'      => __( 'Blog', 'phungdat' ),            'priority'   => 30,        )    );    // Tao blog_title.    $wp_customize->add_setting(    	'blog_title',    	array(			'type'       => 'option', // 'option' || 'theme_mod			'capability' => 'edit_theme_options',			'section'    => 'title_header',			'default'    => __( 'Blog', '' ),		)	);    // Tao blog_title control.	$wp_customize->add_control(		new WP_Customize_Control(			$wp_customize,			'blog_title',			array(				'label'    => __( 'Blog Title', 'phungdat' ),				'section'  => 'title_header',				'settings' => 'blog_title',				'type'     => 'text',			)		)	);//-------------------- Tao section Title meta	// Tao Title meta section	$wp_customize->add_section(    	'title_header_meta',        array(            'title'      => __( 'Description Blog', 'phungdat' ),            'priority'   => 30,        )    );    // Tao meta_title.    $wp_customize->add_setting(    	'meta_title',    	array(			'type'       => 'option', // 'option' || 'theme_mod			'capability' => 'edit_theme_options',			'section'    => 'title_header_meta',			'default'    => __( 'Description Blog', '' ),		)	);    // Tao meta_title control.	$wp_customize->add_control(		new WP_Customize_Control(			$wp_customize,			'meta_title',			array(				'label'    => __( 'Blog Title', 'phungdat' ),				'section'  => 'title_header_meta',				'settings' => 'meta_title',				'type'     => 'text',			)		)	);}add_action( 'customize_register', 'themeslug_customize_register' );